import React from 'react';
import '/home/moglix/Desktop/projectFrontend/my/src/Components/EmployeeComponent/EmployeeComponent.css';
import Popup from 'reactjs-popup';
import 'bootstrap/dist/css/bootstrap.min.css';
import{ProgressBar , Button} from 'react-bootstrap';
import pic from './../../Assets/employee.png';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import Modal from "react-bootstrap/Modal";

class Employee extends React.Component {
    constructor(props){

        super(props);
        this.state = {
            employee:[],
            isOpen: false,
            updateData:[],
            name:''
        }
      }


      componentDidMount()
      {
        fetch('https://mocki.io/v1/8b58f489-d1d2-4b4c-b2bd-02c0603c8112')
        .then(response => response.json())
        .then(result => {
            let tmpArray = []
            for (var i = 0; i < result.data.length; i++) {
                tmpArray.push(result.data[i])
            }
            this.setState({employee:tmpArray})
        })
        .catch(e => {
            console.log(e);
        });

      }
      openModal = () => this.setState({ isOpen: true });
      closeModal=() =>{
         /* this.setState
          const url="";
        axios.post(url, reactData)
               .then(res => console.log('Data send'))
               .catch(err => console.log(err.data))
               this.setState({ isOpen: false });
          */  
      };
      submit(event){
this.setState({name:event.target.values})
      }
      myChangeHandler = (event) => {
        this.setState({name: event.target.value});
      }






    render(){
       console.log(this.state.employee);
       console.log(this.state.name);
        return(

        <div>
            {/*----------------------Navbar----------------- */}

            <nav className="navbar navbar-expand-sm navbar-light bg-light">
            <a className="navbar-brand" href="https://www.moglix.com/" >
                <img src="https://cdn.moglix.com/assets/img/moglix-logo.jpg" id="logo"/></a>
            <ul className="navbar-nav" style={{ listStyle:"none"}}>
                <li className="nav-item">
                    <a className="nav-link" href="#">
                    <Link to={'/'}><h5  style={{}}>Dashboard</h5></Link></a>

                </li>
                <li className="nav-item">
                    <a className="nav-link" href="#" style={{paddingLeft:"2"}}><h5>About</h5></a>
                </li>
            </ul>
        </nav><br />
         {/*----------------------Navbar Ends----------------- */}

         {/*---------------- Side Bar------------------- */}
        <div className="container-fluid">
            <div className="row">
                <div className="col-md-2">
                    <div className="card " id="mc" >
                        <div className="card-body" id="maincard"> 
                        <h4 className="card-title" style={{textDecoration:"underline", textAlign:"center", color:"white"}}>Hi Gagan</h4><br /><br /><br /><br />
                        <ul className="card-text">
                        <Link to={'/'}><li  className="amd">Employee Code -</li></Link><br /><br />
                                <li className="amd"><span className="sidelist">Reporting Manager -</span></li><br /><br />
                                 <li className="amd"><span className="sidelist">Department -</span></li><br /><br />
                                 <button type="button" className="btn btn-danger" onClick={this.openModal}>Add Employee</button><br /><br />
                                 <button type="button" className="btn btn-primary">Update </button><br /><br />
                                 <button type="button" className="btn btn-secondary">Delete</button><br /><br />

                        </ul><br />
                        </div>
                    </div>
               </div>

        {/*----------Main Content------------------------ */}
                <div className="col-md-10 ">
                    <div className="card">
                        <div className="card-body">
                            <h4 style={{textDecoration:"underline"}}>Employee</h4>

</div>
</div><br/>
<div className="card " style={{backgroundColor:"#e8ecf1"}}>
