import React from 'react';
import '/home/moglix/Desktop/projectFrontend/my/src/Components/EmployeeComponent/EmployeeComponent.css';
import Popup from 'reactjs-popup';
import 'bootstrap/dist/css/bootstrap.min.css';
import{ProgressBar} from 'react-bootstrap';
import pic from './../../Assets/employee.png';

class Employee extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            employee:[]
        }
      }
      componentDidMount()
      {
        fetch('https://mocki.io/v1/8b58f489-d1d2-4b4c-b2bd-02c0603c8112')
        .then(response => response.json())
        .then(result => {
            let tmpArray = []
            for (var i = 0; i < result.data.length; i++) {
                tmpArray.push(result.data[i])
            }
            this.setState({employee:tmpArray})
        })
        .catch(e => {
            console.log(e);
        });

      }
    render(){
       console.log(this.state.employee);

        return(
        <div>
            {/*----------------------Navbar----------------- */}

            <nav className="navbar navbar-expand-sm navbar-light bg-light">
            <a className="navbar-brand" href="https://www.moglix.com/" >
                <img src="https://cdn.moglix.com/assets/img/moglix-logo.jpg" id="logo"/></a>
            <ul className="navbar-nav" style={{ listStyle:"none"}}>
                <li className="nav-item">
                    <a className="nav-link" href="#">
                      <h5>Dashboard</h5></a>
                </li>
                <li className="nav-item">
                    <a className="nav-link" href="#" style={{paddingLeft:"2"}}><h5>About</h5></a>
                </li>
            </ul>
        </nav><br />
         {/*----------------------Navbar Ends----------------- */}

         {/*---------------- Side Bar------------------- */}
        <div className="container-fluid">
            <div className="row">
                <div className="col-md-2">
                    <div className="card" id="mc" sticky>
                        <div className="card-body" id="maincard"> 
                        <h4 className="card-title" style={{textDecoration:"underline", textAlign:"center", color:"white",marginTop:"20"}}>Hi Gagan</h4><br /><br /><br /><br />
                        <ul className="card-text">
                                 <li className="amd"><span className="sidelist">Employee Code -</span></li><br /><br />
                                 <li className="amd"><span className="sidelist">Reporting Manager -</span></li><br /><br />
                                 <li className="amd"><span className="sidelist">Department -</span></li><br /><br />
                        </ul><br />
                        </div>
                    </div>
               </div>

        {/*----------Main Content------------------------ */}
                <div className="col-md-10 ">
                    <div className="card">
                        <div className="card-body">

</div>
</div><br/>
<div className="card " style={{backgroundColor:"#e8ecf1"}}>
<div className="row">

                        {this.state.employee.map((emp)=>(
                            <div className="col-sm">

                            <div className="card allCard">
                               <div className="card-body">
                             <img src={pic} alt="..." style={{height:"80px"  ,marginLeft:"110px"}} />
                            <h5 className="card-title empName" style={{textDecoration:"underline", textAlign:"center"}}>{emp.employee_name}</h5>
                            <h6 className="listhead" style={{ textAlign:"center"}}>Designation</h6>
                            <h6 className="listhead" style={{ textAlign:"center"}}>emial </h6><br/>
                             <ul className="card-text">
                                 <li className="am"><span className="listhead">Employee Code -</span><span className="ans"> gagan</span></li><br />
                                 <li className="am"><span className="listhead">Reporting Manager -</span><span className="ans"> gagandeep</span></li><br />
                                 <li className="am"><span className="listhead">Department -</span><span className="ans"> gagan</span></li><br />




                             </ul><br />
                             <div class="card-footer">
                             <h6 className="listhead text-muted">Years Completed </h6>
                             <ProgressBar animated variant="info" now={1} max={10} />
                             </div>
                             </div>

                             </div><br/>
                        </div>


                        ))}



                            </div>
                        </div>
                  </div>     









            </div>
        </div>

















        </div>
            );
    }
}
export default Employee;
